events.inlineCode:
var servoid = 'http://localhost:8080'

objects.forEach(q => {
    var g = z => q.getVariables().get(z).getAsNumber();
    var r = z => q.getVariables().get(z).getAsString();
    var s = (z, p) => q.getVariables().get(z).setNumber(p);
    if(g('doRegister')){
        fetch(servoid + '/game/create_trap', {
            method: 'POST',
            body: JSON.stringify({
                id: r('uuid'),
                state: 'ready',
                trap: 'spikes',
                color: '#ff5733',
                text: 'Spikes'
            })
        }).then(function(response) {
            s('doRegister', 0)
            s('initialised', 1)
            alert('spieks registered')
        }).catch(function(e){
            //alert(e.message);
        })
    }else if(g('doRemove') == 1){
        fetch(servoid + '/game/destroy_trap', {
            method: 'POST',
            body: JSON.stringify({
                id: r('uuid')
            })
        }).then(function(response){
            s('doRemove', 0);
            s('pufferfish', 1);
            alert(g('doRemove'));
            alert('spieks deregistered');
        })
    }else{
        fetch(servoid + '/game/trap_status/' + r('uuid')).then(res => res.json()).then(function(waffle){
            alert('glarghbalg' + waffle);
        })
    }
})
